import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'

export const signUpUser = createAsyncThunk(
    async({ name, email, password})
)

export const userSlice = createSlice({
    name: "user",
    initialState: {
        username: "",
        email: "",
        isFetching: false,
        isSuccess: false,
        isError: false,
        errorMessage: "",
    },
    reducers: {

    },
    extraReducers: {

    },
})

export default userSlice.reducer;
