import { Nav } from "react-bootstrap"
import { Navbar, NavDropdown } from "react-bootstrap";
import { Container, Row, Col } from "react-bootstrap";
import Button from 'react-bootstrap/Button'
import HalfLogo from '../../assets/images/showyourgroove-halflogo.png'
import { Link } from "react-router-dom";

import { Route, Switch } from 'react-router-dom'
import React, { useState, useEffect, useCallback } from 'react'
import { useDispatch, useSelector } from 'react-redux';

import Profile from '../../components/user/profile';
import BoardUser from '../../components/user/BoardUser';
import BoardAdmin from '../../components/user/BoardAdmin';
import { logout } from "../../state/authSlice"
import eventBus from '../../common/EventBus';




const TopNav = () => {
    const [showModeratorBoard, setShowModeratorBoard] = useState(false);
    const [showAdminBoard, setShowAdminBoard] = useState(false);

    const { user: currentUser } = useSelector((state) => state.auth);
    const dispatch = useDispatch();

    const logOut = useCallback(() => {
        dispatch(logout());
    }, [dispatch]);

    useEffect(() => {
        if (currentUser) {
            setShowModeratorBoard(currentUser.roles.includes("ROLE_MODERATOR"));
            setShowAdminBoard(currentUser.roles.includes("ROLES_ADMIN"));
        } else {
            setShowModeratorBoard(false);
            setShowAdminBoard(false);
        }

        eventBus.on("logout", () => {
            logOut();
        });

        return () => {
            eventBus.remove("logout");
        };
    }, [currentUser, logOut]);


    return (
        <Navbar className="bg-secondary pb-1 ps-5" expand="lg" >
            <Container fluid className="d-flex justify-content-center ms-5 ps-5">
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <div>
                    <Navbar.Collapse id="basic-navbar-nav" >
                        <Nav.Link href="/#">
                            <h2 className="text-dark">Home</h2>
                        </Nav.Link>
                        <Nav.Link href="/aboutus">
                            <h2 className="text-dark">About</h2>
                        </Nav.Link>
                    </Navbar.Collapse>
                </div>
                <Navbar.Brand href="#home"><img src={HalfLogo} /></Navbar.Brand>
                <div>
                    <Navbar.Collapse>
                        <Nav.Link href="/firstchatroom">
                            <h2 className="text-dark">Chat</h2>
                        </Nav.Link>
                        <Nav.Link href="/support">
                            <h2 className="text-dark">Support</h2>
                        </Nav.Link>
                    </Navbar.Collapse>
                </div>
                <Button href="/login" className="btn-dark me-3">Login</Button>
                <Button href="/register" className="btn-dark">Register</Button>
                <NavDropdown title="Profile" id="basic-nav-dropdown">
                    {showModeratorBoard && (
                        <NavDropdown.Item>
                            <Link to="/mod">
                                Moderator Board
                            </Link>
                        </NavDropdown.Item>
                    )}

                    {showAdminBoard && (
                        <NavDropdown.Item>
                            <Link to="/admin">
                                Admin Board
                            </Link>
                        </NavDropdown.Item>
                    )}

                    {currentUser && (
                        <NavDropdown.Item>
                            <Link to="/user">
                                User
                            </Link>
                        </NavDropdown.Item>
                    )}



                    <NavDropdown.Item>
                        <Link to="/profile">
                            {currentUser.user}
                        </Link>
                    </NavDropdown.Item>
                    <NavDropdown.Item>
                        <Link to="/user">
                            User
                        </Link>
                    </NavDropdown.Item>


                    <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                    <NavDropdown.Divider />
                    <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
                </NavDropdown>
            </Container>
        </Navbar>
    )
}

export default TopNav;

<Navbar className="bg-secondary pb-1" expand="lg" >
    <Container fluid className="d-flex justify-content-center">
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Row>
            <Col>
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="me-auto">
                        <Nav.Link href="/#">
                            <h2 className="text-dark py-3">Home</h2>
                        </Nav.Link>
                        <Nav.Link href="/aboutus">
                            <h2 className="text-dark py-3">About</h2>
                        </Nav.Link>
                    </Nav>
                </Navbar.Collapse>
            </Col>
            <Col className="d-flex justify-content-center">
                <Navbar.Brand href="#home"><img src={HalfLogo} /></Navbar.Brand>
            </Col>
            <Col>
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="me-auto">
                        <Nav.Link href="/firstchatroom">
                            <h2 className="text-dark py-3">Chat</h2>
                        </Nav.Link>
                        <Nav.Link href="/support">
                            <h2 className="text-dark py-3">Support</h2>
                        </Nav.Link>
                    </Nav>
                </Navbar.Collapse>
            </Col>
        </Row>
    </Container>
</Navbar>
